import React, { useEffect } from "react";
// react plugin for creating charts
// @material-ui/core
import { Form, Input,} from 'antd';
import { makeStyles } from "@material-ui/core/styles";

import Button from "components/CustomButtons/Button.js";

// core components
import axios from "axios";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Table from "components/Table/Table.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import styles from "assets/jss/material-dashboard-react/views/dashboardStyle.js";
import { useState } from "react";



const useStyles = makeStyles(styles);
const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};
const validateMessages = {
  required: '${label} is required!',
  types: {
    email: '${label} is not a valid email!',
    number: '${label} is not a valid number!',
  },
  number: {
    range: '${label} must be between ${min} and ${max}',
  },
};
/* eslint-enable no-template-curly-in-string */


export default function Dashboard() {
  //gallery upload
  const [image, setImage] = useState("")
  const submitImage = async () => {
    // e.preventDefault()
    var imagedata = {image}

    console.log(typeof(imagedata));

    //services post
   var res = await  fetch('http://localhost:4000/Gallery/',{
    method : "POST",
    headers: {
       "Content-type": "application/json",
       "Accept" : "application/json",
      },
     body  : JSON.stringify(imagedata)

  })
   console.log(res)

}


  //services set state
  const [headline, setHeadline] = useState("")
  const [discription, setDiscription] = useState("")
  const [icon, setIcon] = useState("")
  const submitData = async () => {
    // e.preventDefault()
    var servicesdata = {headline, discription, icon}

    console.log(typeof(servicesdata));

    //services post
   var res = await  fetch('http://localhost:4000/Services/',{
    method : "POST",
    headers: {
       "Content-type": "application/json",
       "Accept" : "application/json",
      },
     body  : JSON.stringify(servicesdata)

  })
   console.log(res)

}

//Doctor data
const [docdata,setDocdata] = useState([])
var config ={
 headers: {
   "Access-Control-Allow-Origin": "*",
   "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
 }
}
useEffect(() => {
 axios

     .get("http://localhost:4000/DentalAdmin/")
     .then(res => {
       setDocdata(res.docdata)


     })
}, [])
console.log(docdata)


  //appointment
  const classes = useStyles();
  const [data,setData] = useState([])
   var config ={
    headers: {
      "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
    }
  }
  useEffect(() => {
    axios

        .get("http://localhost:4000/Appointment/")
        .then(res => {
          setData(res.data)


        })
  }, [])
  console.log(data)

  return (
    <div>


      <GridContainer>
        <GridItem xs={12} sm={12} md={6}>

        <table class="table">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    {docdata.map((Element)=>(
      <tr>
      <th scope="row">1</th>
      <td>{Element.name}</td>
      <td>{Element.introduction}</td>
      <td>{}</td>
    </tr>
    ))}
    {/* <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td>Larry</td>
      <td>the Bird</td>
      <td>@twitter</td>
    </tr> */}
  </tbody>
</table>

        </GridItem>
        <GridItem xs={12} sm={12} md={6}>
          <Card>
            <CardHeader color="warning">
              <h4 className={classes.cardTitleWhite}>Appointment Status</h4>
              <p className={classes.cardCategoryWhite}>
                Appointments booked
              </p>
            </CardHeader>
            <CardBody>
              <Table
                tableHeaderColor="warning"
                tableHead={["ID", "Name", "Discription", "Timing"]}
                tableData={data}
              />
            </CardBody>
          </Card>
        </GridItem>
        <GridItem xs={12} sm={12} md={6}>

      <Form {...layout} name="nest-messages" validateMessages={validateMessages}>
        <h1>Upload Services</h1>
      <Form.Item name={['user', 'headline']} label="Headline" 
