import { makeStyles } from "@material-ui/core/styles";
import { Form, Input, InputNumber,Upload, message } from 'antd';
import Button from "components/CustomButtons/Button.js";
import { useEffect } from 'react';
import { UploadOutlined } from '@ant-design/icons';

const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};
/* eslint-disable no-template-curly-in-string */

const validateMessages = {
  required: '${label} is required!',
  types: {
    email: '${label} is not a valid email!',
    number: '${label} is not a valid number!',
  },
  number: {
    range: '${label} must be between ${min} and ${max}',
  },
};
/* eslint-enable no-template-curly-in-string */



const styles = {
  cardCategoryWhite: {
    color: "rgba(255,255,255,.62)",
    margin: "0",
    fontSize: "14px",
    marginTop: "0",
    marginBottom: "0",
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
  },
};
//  useEffect(() => {
//    axios

//        .post("http://localhost:4000/DentalAdmin/")
//        .then(res => {
//          setData(res.onFinish)


//        })
//  }, [])
const useStyles = makeStyles(styles);

export default function UserProfile() {
  const props = {
  name: 'file',
  action: 'https://www.mocky.io/v2/5cc8019d300000980a055e76',
  headers: {
    authorization: 'authorization-text',
  },
  onChange(info) {
    if (info.file.status !== 'uploading') {
      console.log(info.file, info.fileList);
    }
    if (info.file.status === 'done') {
      message.success(`${info.file.name} file uploaded successfully`);
    } else if (info.file.status === 'error') {
      message.error(`${info.file.name} file upload failed.`);
    }
  },
};


const [name, setName] = useState('')
const [email, setEmail] = useState('')
const [discription, setDiscription] = useState('')
const [timings, setTimings] = useState('')
const submitData = async (event) => {
    event.preventDefault()
    var userdata = {userdata}
    axios.post("http://localhost:4000/Appointment/", {

        headers: {
            "Content-type": "application/json",
            "Accept": "application/json",
        },
        body: JSON.stringify(userdata)

    })
        .then(res => {
            console.log(res)


        })


}
  return (
    <Form {...layout} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}>
      <Form.Item
        id="name"
        name='name'
        label="Name"
        value={name}
        type="text"
        rules={[
          {
            required: true,
          },
        ]}
        onFinish={(e) => { setName(e.target.value) }}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name={['user', 'email']}
        label="Email"
        rules={[
          {
            type: 'email',
          },
        ]}
        onFinish={(e) => { setEmail(e.target.value) }}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name={['user', 'phone']}
        label="Phone"
        rules={[
          {
            type: 'number',
            min: 1000000000,
            max: 9999999999,
          },
        ]}
        onFinish={(e) => { setName(e.target.value) }}
      >
        <InputNumber />
      </Form.Item>
      <Form.Item name={['user', 'instagram']} label="Instagram" onFinish={(e) => { setName(e.target.value) }}>
        <Input />
      </Form.Item>
      <Form.Item name={['user', 'facebook']} label="FaceBook" onFinish={(e) => { setName(e.target.value) }}>
        <Input />
      </Form.Item>
      <Form.Item name={['user', 'twitter']} label="Twitter" onFinish={(e) => { setName(e.target.value) }}>
        <Input />
      </Form.Item>
      <Form.Item name={['user', 'linkedin']} label="LinkedIn" onFinish={(e) => { setName(e.target.value) }}>
        <Input />
      </Form.Item>
      <Form.Item name={['user', 'introduction']} label="Introduction" onFinish={(e) => { setName(e.target.value) }}>
        <Input.TextArea />
      </Form.Item>
      <Upload {...props}>
     <Button icon={<UploadOutlined />}>Click to Upload</Button>
   </Upload>
      <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }} onFinish={(e) => { setName(e.target.value) }}>
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>
    </Form>
  );
}



