import React, { useEffect } from "react";
// react plugin for creating charts
// @material-ui/core
import { Form, Input,} from 'antd';
import { makeStyles } from "@material-ui/core/styles";

import Button from "components/CustomButtons/Button.js";

// core components
import axios from "axios";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import TableData from "components/Table/Table.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import styles from "assets/jss/material-dashboard-react/views/dashboardStyle.js";
import { useState } from "react";

import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Eclair', 262, 16.0, 24, 6.0),
  createData('Cupcake', 305, 3.7, 67, 4.3),
  createData('Gingerbread', 356, 16.0, 49, 3.9),
];


const useStyles = makeStyles(styles);
const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};
const validateMessages = {
  required: '${label} is required!',
  types: {
    email: '${label} is not a valid email!',
    number: '${label} is not a valid number!',
  },
  number: {
    range: '${label} must be between ${min} and ${max}',
  },
};
/* eslint-enable no-template-curly-in-string */


export default function Dashboard() {
  //gallery upload
  const [image, setImage] = useState("")
  const submitImage = async () => {
    // e.preventDefault()
    var imagedata = {image}

    console.log(typeof(imagedata));

    //services post
   var res = await  fetch('http://localhost:4000/Gallery/',{
    method : "POST",
    headers: {
       "Content-type": "application/json",
       "Accept" : "application/json",
      },
     body  : JSON.stringify(imagedata)

  })
   console.log(res)

}


  //services set state
  const [headline, setHeadline] = useState("")
  const [discription, setDiscription] = useState("")
  const [icon, setIcon] = useState("")
  const submitData = async () => {
    // e.preventDefault()
    var servicesdata = {headline, discription, icon}

    console.log(typeof(servicesdata));

    //services post
   var res = await  fetch('http://localhost:4000/Services/',{
    method : "POST",
    headers: {
       "Content-type": "application/json",
       "Accept" : "application/json",
      },
     body  : JSON.stringify(servicesdata)

  })
   console.log(res)

}


  //appointment
  const classes = useStyles();
  const [data,setData] = useState([])
   var config ={
    headers: {
      "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
    }
  }
  useEffect(() => {
    axios

        .get("http://localhost:4000/Appointment/")
        .then(res => {
          setData(res.data)


        })
  }, [])
  console.log(data)

  return (
    <div>


      <GridContainer>
        <GridItem xs={12} sm={12} md={6}>
        <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Dessert (100g serving)</TableCell>
            <TableCell align="right">Calories</TableCell>
