import React, { useEffect } from "react";
// react plugin for creating charts
// @material-ui/core
import { Form, Input, InputNumber,Upload} from 'antd';
import { makeStyles } from "@material-ui/core/styles";
import BugReport from "@material-ui/icons/BugReport";
import Code from "@material-ui/icons/Code";
import Cloud from "@material-ui/icons/Cloud";
import Button from "components/CustomButtons/Button.js";

// core components
import axios from "axios";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Table from "components/Table/Table.js";
import Tasks from "components/Tasks/Tasks.js";
import CustomTabs from "components/CustomTabs/CustomTabs.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import { bugs, website, server } from "variables/general.js";
import styles from "assets/jss/material-dashboard-react/views/dashboardStyle.js";
import { useState } from "react";

const useStyles = makeStyles(styles);
const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};
const validateMessages = {
  required: '${label} is required!',
  types: {
    email: '${label} is not a valid email!',
    number: '${label} is not a valid number!',
  },
  number: {
    range: '${label} must be between ${min} and ${max}',
  },
};
/* eslint-enable no-template-curly-in-string */






export default function Dashboard() {

  const submitData = async () => {
    // e.preventDefault()
    var userdata = {facebook, twitter, linkedin, introduction, image}





    // axios

    // .post('http://localhost:4000/DentalAdmin',

    //  userdata,

    //  {

    //   headers: { "content-type": "application/json" },

    // })

    // .then(function (response) {

    //   // handle success

    //   console.log("response", response);
    // })

    console.log(typeof(userdata));


   var res = await  fetch('http://localhost:4000/DentalAdmin/',{
    method : "POST",
    headers: {
       "Content-type": "application/json",
       "Accept" : "application/json",
      },
     body  : JSON.stringify(userdata)

  })
   console.log(res)

}



  const classes = useStyles();
  const [data,setData] = useState([])
  const [image, setImage] = useState('')
   var config ={
    headers: {
      "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
    }
  }
  useEffect(() => {
    axios

        .get("http://localhost:4000/Appointment/")
        .then(res => {
          setData(res.data)


        })
  }, [])
  console.log(data)

  return (
    <div>


      <GridContainer>
        <GridItem xs={12} sm={12} md={6}>
          <div>
            <h1>Upload images in Gallery</h1>
        {image && (
        <div>
        <img alt="not fount" width={"250px"} src={URL.createObjectURL(image)} />
        <br />
        <button onClick={()=>setImage(null)}>Remove</button>
        </div>
      )}
      <br />

      <br /> 
      <input
        type="file"
        name="myImage"
        onChange={(event) => {
          console.log(event.target.files[0]);
          setImage(event.target.files[0]);
        }}
      /> 
      <br/>

      <br/>

      <button>Click to Submit</button>

      </div>
        </GridItem>
        <GridItem xs={12} sm={12} md={6}>
          <Card>
            <CardHeader color="warning">
              <h4 className={classes.cardTitleWhite}>Appointment Status</h4>
              <p className={classes.cardCategoryWhite}>
                Appointments booked
              </p>
            </CardHeader>
            <CardBody>
              <Table
                tableHeaderColor="warning"
                tableHead={["ID", "Name", "Discription", "Timing"]}
                tableData={data}
              />
            </CardBody>
          </Card>
        </GridItem>
        <GridItem xs={12} sm={12} md={6}>

      <Form {...layout} name="nest-messages" validateMessages={validateMessages}>
        <h1>Upload Services</h1>
      <Form.Item name={['user', 'instagram']} label="Instagram" 
       onChange={(e) => { setInstagram(e.target.value) }}>
        <Input />
      </Form.Item>
      <Form.Item name={['user', '']} label="FaceBook" 
      // value={facebook} onChange={(e) => { setFacebook(e.target.value) }}
      >
        <Input />
      </Form.Item>
      <Form.Item name={['user', 'twitter']} label="Twitter" 
      // value={twitter} onChange={(e) => { setTwitter(e.target.value) }}
      >
        <Input />
      </Form.Item>
      <Form.Item name={['user', 'linkedin']} label="LinkedIn" 
      // value={linkedin} onChange={(e) => { setLinkedin(e.target.value) }}
      >
        <Input />
      </Form.Item>
      <Form.Item name={['user', 'introduction']} label="Introduction" 
      // value={introduction} onChange={(e) => { setIntroduction(e.target.value) }}
      >
        <Input.TextArea />
      </Form.Item>
      {image && (
        <div>
        <img alt="not fount" width={"250px"} src={URL.createObjectURL(image)} />
        <br />
        <button onClick={()=>setImage(null)}>Remove</button>
        </div>
      )}
      <br />

      <br /> 
      <input
        type="file"
        name="myImage"
        onChange={(event) => {
          console.log(event.target.files[0]);
          setImage(event.target.files[0]);
        }}
      />     
      {/* <Upload {...props}>
     <Button icon={<UploadOutlined />} Input value={image} onChange={(e) => {setImage(e.target.file) }}>Click to Upload</Button>
   </Upload> */}
      <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
        <Button type="primary" htmlType="submit" onClick={(submitData)} >
          Submit
        </Button>
      </Form.Item>
    </Form>

        </GridItem>
      </GridContainer>

    </div>
  );
}
